mapper.select:
select *
from order_record
where user_id = #{userId} limit #{offset}
    , #{pageSize}

select *
from order_record
where doctor_name = #{doctorName} limit #{offset}
    , #{pageSize}

select count(*)
from order_record
where doctor_name = #{doctorName}

mapper.update:
update order_record
set is_send     = #{idSend},
    notify_time = #{notifyTime}
where order_id = #{orderId}

update order_record
set is_finish = 1
where order_id = #{orderId}
